function getDaysHoursSeconds(t){var n=Math.abs(t-new Date)/1e3,o=Math.floor(n/86400);n-=86400*o;var e=Math.floor(n/3600)%24;n-=3600*e;var a=Math.floor(n/60)%60;return n-=60*a,[o,e,a,Math.floor(n%60)]}function initTimer(t){setInterval(function(){const n=getDaysHoursSeconds(t);$(".hero__dates__element").each(function(t,o){$(o).find("h3").text(n[t])})},1e3)}function initSmoothScroll(){$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click(function(t){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var n=$(this.hash);if(n=n.length?n:$("[name="+this.hash.slice(1)+"]"),n.length){t.preventDefault();const o={scrollTop:n.offset().top-50};$("html, body").animate(o,1e3,function(){var t=$(n);if(t.focus(),t.is(":focus"))return!1;t.attr("tabindex","-1"),t.focus()})}}})}function fixTable(){$("table").addClass("table").addClass("table-hover")}function initTopButton(){const t=$("#scroll-top");t.hide(),$(window).scroll(function(){$(this).scrollTop()>500?t.fadeIn(500):t.fadeOut(500)}),t.click(function(){const t={scrollTop:0};$("html, body").animate(t,1e3)})}$(function(){initTimer(new Date(2018,6,2)),initSmoothScroll(),fixTable(),initTopButton(),$(".navbar-toggler").click(function(){const t=1.5*$(".navbar-collapse").height(),n=$(".hero"),o=$(".hero__dates");parseInt(n.css("padding-top"),10)>0?(n.animate({paddingTop:0}),o.fadeIn()):(n.animate({paddingTop:t}),o.fadeOut())})});